<template>
   <div class="signupform">

    <h1>Signup</h1>

    <v-text-field
    label="Name"
    v-model="form.name"
    type="text"
    required class="input">
    </v-text-field>
    <span class="error_message" v-if="errors.name">{{errors.name[0]}}</span>

    <v-text-field
    label="Email"
    v-model="form.email"
    type="email"
    required class="input">
    </v-text-field>
    <span class="error_message" v-if="errors.email">{{errors.email[0]}}</span>

     <v-text-field
     label="Password"
    v-model="form.password"
    type="password"
    required class="input">
     </v-text-field>
    <span class="error_message" v-if="errors.password">{{errors.password[0]}}</span>

     <v-text-field
     label="Confirm password"
    v-model="form.password_confirmation"
    type="password"
    required class="input">
     </v-text-field>

    <button @click.prevent="signup" :class="btn">
       <transition name="done" mode="out-in">
         <span class="label" :key="second_done">
            {{ second_done ? 'Done' : 'Signup'}}
         </span>
       </transition>
         <span class="spinner"></span>
     </button>

    <!-- <router-link to="/login">
       <button class="login_btn">Login</button>
     </router-link> -->

   </div>
</template>

<script>
import User from "../../Helpers/User.js"
export default {
  data(){
      return{
          form:{
              name:null,
              email:null,
              password:null,
              password_confirmation:null//ここはlaravelのvalidationでconfirmを使うために_confirmationとしなければだめ
          },
          errors:[],
          first_done:false,
          second_done:false
      }
  },
  methods:{
      signup(){
          var self = this
          self.first_done=true;
         axios.post("/api/auth/signup",self.form)
         .then(res =>{
            User.responseAfterLogin(res)
            setTimeout(self.changeSuccess,2000);
            // window.location="/forum"
            // self.$router.push({name:'forum'})
         })
         .catch(error => {
             self.errors = error.response.data.errors
             setTimeout(self.changeFalse,2000);
         })//ここからerrors['email']とかでアクセス、各一番目をとっていけばいいのでerrors['email'][0]

      },
      changeSignup(){
       this.$store.dispatch("changeSignupFlag");
   },
    changeSuccess(){
       this.first_done=false;
       this.second_done=true;
       setTimeout(this.fireLoading,2000);
   },
   changeFalse(){
       this.first_done=false;
   },
   fireLoading(){
      this.$emit("startLoading")
   }
  },
  created(){
      if(this.$store.getters.userLoggedIn){
           window.location="/welcome"
        //   this.$router.push({name:'forum'});
      }
  },
   computed:{
      btn(){
          return this.first_done ? "btn_first" : "btn";
      }
  }
}
</script>

