        font-size: 1.3rem;
        cursor: pointer;
        outline: none;

        background: #2aca76;
        color:#fff;
        border-radius:2px;
        border: 1px solid transparent;

        overflow: hidden;
        width: 10rem;

        .spinner{

            left:50%;
            margin-left: -1.6rem;

            position: absolute;
            width: 3.2rem;
            height: 3.2rem;
            top:50%;
            margin-top: -1.6rem;
            opacity: 0;
            background-image: url( data:image/gif;base64,);
        }
    }
    .btn:hover{
        border-color: rgba( 0, 0, 0, 0.07 );
		background-color: #2fe688;
    }
    .btn .label{
        opacity:1;
    }

    .btn,
    .btn .spinner,
    .btn .label{
        transition: .3s cubic-bezier(0.175,0.895,0.320,1.275);
    }

    .btn_first,
    .btn_first .spinner,
    .btn_first .label{
        transition: .3s cubic-bezier(0.175,0.895,0.320,1.275);
    }
    //.btn_firstとかすると全部書き直さないといけないけど.btn[]とすると必要な分は上書きになる？

    .btn_first{
        position: relative;
        border: 0;
